-- Drop the database if it exists
IF EXISTS (SELECT name FROM sys.databases WHERE name = N'ChildrenCare')
BEGIN
    USE master;
    ALTER DATABASE ChildrenCare SET SINGLE_USER WITH ROLLBACK IMMEDIATE;
    DROP DATABASE [ChildrenCare];
END

-- Create the new database
CREATE DATABASE ChildrenCare;

-- Switch to the new database
USE ChildrenCare;

-- Create the Users table
CREATE TABLE Users (
    UserID INT IDENTITY(1,1) PRIMARY KEY,
	Address NVARCHAR(300) COLLATE Vietnamese_CI_AS NOT NULL,
    Email VARCHAR(100) NOT NULL UNIQUE,
    Password VARCHAR(50) NOT NULL,
    FirstName NVARCHAR(50) COLLATE Vietnamese_CI_AS NOT NULL,
    LastName NVARCHAR(50) COLLATE Vietnamese_CI_AS NOT NULL,
    Gender VARCHAR(10) NOT NULL,
    PhoneNumber VARCHAR(20) NOT NULL,
    ProfileImage VARCHAR(MAX),
	Status bit default 0
);

-- Create the CategoryService table
CREATE TABLE CategoryService (
    CategoryID INT IDENTITY(1,1) PRIMARY KEY,
    CategoryName VARCHAR(50) COLLATE Vietnamese_CI_AS NOT NULL,
    Description VARCHAR(MAX) COLLATE Vietnamese_CI_AS NOT NULL 
);

-- Create the Services table
CREATE TABLE Services (
    ServiceID INT IDENTITY(1,1) PRIMARY KEY,
    Title VARCHAR(100) COLLATE Vietnamese_CI_AS UNIQUE NOT NULL,
    Thumbnail NVARCHAR(MAX),
    CategoryID INT NOT NULL,
    OriginalPrice DECIMAL(10, 2) NOT NULL,
    SalePrice DECIMAL(10, 2),
    ServiceDetails TEXT COLLATE Vietnamese_CI_AS,
    BriefInfo TEXT COLLATE Vietnamese_CI_AS,
    UpdateDate DATE,
	Status BIT NOT NULL DEFAULT 1, 
    FOREIGN KEY (CategoryID) REFERENCES CategoryService(CategoryID)
);

-- Create the Posts table
CREATE TABLE Posts (
    PostID INT IDENTITY(1,1) PRIMARY KEY,
    Title VARCHAR(100) NOT NULL,
    Content TEXT NOT NULL,
    BriefInfo VARCHAR(MAX),
    Thumbnail TEXT,
	Counts INT Default 0,
    AuthorID INT NOT NULL, 
    ServiceID INT NOT NULL,
    CreatedDate DATETIME NOT NULL,
    CategoryPost VARCHAR(100) NOT NULL,
	StatusPost BIT NOT NULL DEFAULT 1
    FOREIGN KEY (AuthorID) REFERENCES Users(UserID),
    FOREIGN KEY (ServiceID) REFERENCES Services(ServiceID)
);
-- Create the Comments table
CREATE TABLE Comments (
    CommentID INT IDENTITY(1,1) PRIMARY KEY,
    UserID INT NOT NULL,
    PostID INT NOT NULL,
    Content NVARCHAR(MAX) COLLATE Vietnamese_CI_AS NOT NULL,
    CommentDate DATETIME NOT NULL,
    FOREIGN KEY (UserID) REFERENCES Users(UserID),
    FOREIGN KEY (PostID) REFERENCES Posts(PostID)
);

-- Create the Cart table
CREATE TABLE Cart (
    CartID INT PRIMARY KEY IDENTITY(1,1),
    UserID INT,
    NumberOfPerson INT,
    FOREIGN KEY (UserID) REFERENCES Users(UserID)
);


-- Create the Staff table
CREATE TABLE Staff (
    StaffID INT PRIMARY KEY IDENTITY(1,1),
    StaffName NVARCHAR(255) COLLATE Vietnamese_CI_AS,
    Email VARCHAR(100) NOT NULL UNIQUE,
    Password VARCHAR(50) NOT NULL,
    FullName NVARCHAR(50) COLLATE Vietnamese_CI_AS NOT NULL,
    Gender VARCHAR(10) NOT NULL,
    PhoneNumber VARCHAR(20) NOT NULL,
    ProfileImage VARCHAR(MAX),
    StaffRole VARCHAR(50) NOT NULL,
    Rank NVARCHAR(50),
    Specialty NVARCHAR(50),
	Introduction NVARCHAR(MAX)
);

-- Create the CartItems table
CREATE TABLE CartItems (
    CartItemID INT PRIMARY KEY IDENTITY(1,1),
    ServiceID INT NOT NULL, 
    Quantity INT NOT NULL,
    TotalCost DECIMAL(10, 2),
    CONSTRAINT FK_CartItems_ServiceID FOREIGN KEY (ServiceID) REFERENCES Services(ServiceID) 
);

-- Create the Settings table
CREATE TABLE Settings (
  SettingID INT IDENTITY(1,1) PRIMARY KEY,
  Type VARCHAR(50) NOT NULL,
  Name VARCHAR(100) NOT NULL,
  Value TEXT,
  Description TEXT,
  Status VARCHAR(20) NOT NULL
);

-- Create the Slider table
CREATE TABLE Slider (
    SliderId INT IDENTITY(1,1) PRIMARY KEY,
    Title VARCHAR(50) NOT NULL,
	Brief TEXT,
    Images TEXT,
    Backlink VARCHAR(200),
    Status VARCHAR(20)
);

CREATE TABLE Children (
    ChildID INT IDENTITY(1,1) PRIMARY KEY,
    UserID INT NOT NULL,
    ChildName NVARCHAR(100) COLLATE Vietnamese_CI_AS NOT NULL,
    Birthday DATE,
	Status TEXT,
	Gender VARCHAR(10) NOT NULL,
	Avatar VARCHAR(MAX),
    FOREIGN KEY (UserID) REFERENCES Users(UserID)
);


-- Create the Reservations table
CREATE TABLE Reservations (
    ReservationID INT IDENTITY(1,1) PRIMARY KEY,
    UserID INT NOT NULL,
    ServiceID INT NOT NULL,
	StaffID INT NOT NULL,
	ChildID INT NOT NULL,
    ReservationDate DATE NOT NULL,
    ReservationSlot INT NOT NULL,
	CreatedDate DATETIME NOT NULL,
    Cost DECIMAL(10, 2) NOT NULL,
    Status VARCHAR(100) NOT NULL,
    FOREIGN KEY (UserID) REFERENCES Users(UserID),
    FOREIGN KEY (ServiceID) REFERENCES Services(ServiceID),
	FOREIGN KEY (StaffID) REFERENCES Staff(StaffID),
	FOREIGN KEY (ChildID) REFERENCES Children(ChildID)
);


-- Create the MedicalExaminations table
CREATE TABLE MedicalExaminations (
	MedicalExaminationID INT PRIMARY KEY IDENTITY(1,1),
	UserID INT,
	ChildrenID INT,
	StaffID INT,
	ExaminationDate DATETIME,
	ServiceID INT,
	MedicalPrescription NVARCHAR(MAX),
	Disease NVARCHAR(100),
	FOREIGN KEY (UserID) REFERENCES Users(UserID),
	FOREIGN KEY (ChildrenID) REFERENCES Children(ChildID),
	FOREIGN KEY (StaffID) REFERENCES Staff(StaffID),
	FOREIGN KEY (ServiceID) REFERENCES Services(ServiceID)
);
-- Create the Feedbacks table
CREATE TABLE Feedbacks (
    FeedbackID INT IDENTITY(1,1) PRIMARY KEY,
    UserID INT NOT NULL,
    MedicalExaminationID INT NOT NULL,
    Content NVARCHAR(MAX) COLLATE Vietnamese_CI_AS NOT NULL,
    FeedbackDate DATETIME NOT NULL,
    RatedStar INT,
	FStatus VARCHAR(50),
    FOREIGN KEY (UserID) REFERENCES Users(UserID),
    FOREIGN KEY (MedicalExaminationID) REFERENCES MedicalExaminations(MedicalExaminationID)
);

-- Create the Statistic table
CREATE TABLE Statistic (
    StatisticID INT PRIMARY KEY IDENTITY(1,1),
    NewReservations INT,
    CancelledReservations INT,
    SubmittedReservations INT,
    TotalRevenues DECIMAL(10, 2),
    NewCustomer INT,
    NewReservation INT,
    AverageStarRating DECIMAL(3, 2),
    ReservationCountsByDay INT,
    StartDate DATETIME,
    EndDate DATETIME
);

-- Create the ServiceStaff table
CREATE TABLE ServiceStaff (
    ServiceStaffID INT PRIMARY KEY IDENTITY(1,1),
    ServiceID INT,
    StaffID INT,
    FOREIGN KEY (ServiceID) REFERENCES Services(ServiceID),
    FOREIGN KEY (StaffID) REFERENCES Staff(StaffID)
);

CREATE TABLE Manager (
    ManagerID INT IDENTITY(1,1) PRIMARY KEY,
    Email  VARCHAR(100) NOT NULL UNIQUE,
	Password  VARCHAR(100) NOT NULL,
    ManagerName NVARCHAR(100) COLLATE Vietnamese_CI_AS NOT NULL,
);


CREATE TABLE StaffSchedules (
    ScheduleID INT IDENTITY(1,1) PRIMARY KEY,
    StaffID INT NOT NULL,
    Workday Date NOT NULL,
    Slot int NOT NULL,
    FOREIGN KEY (StaffID) REFERENCES Staff(StaffID)
);

